mapper.update:
UPDATE NEWS_NEWS
TITLE = #{TITLE}, SUBTITLE = #{SUBTITLE}, IMGURL = #{IMGURL}, NEWSTYPE = #{NEWSTYPE}, MSORT = #{MSORT}, CONTENT = #{CONTENT}, ABSTRACT = #{ABSTRACT}
WHERE NEWS_ID = #{NEWS_ID}

UPDATE NEWS_NEWS
AUTHOR = #{AUTHOR}, CREATETIME = #{CREATETIME}, MODIFYTIME = #{MODIFYTIME}, VIEWCOUNT = #{VIEWCOUNT},
WHERE NEWS_ID = #{NEWS_ID}
